import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import TopMenu from "../TopMenu";
import "./_header.scss";

const Header = () => {
  const { cart } = useSelector((obj) => obj);
  const userdetails = JSON.parse(sessionStorage.getItem("userdetails"));
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const data = [
    { name: "Long coat", category: "Outfit" },
    { name: "Dress", category: "Outfit" },
    { name: "Denim jeans", category: "Outfit" },
    { name: "Leather bag", category: "Accessories" },
    { name: "Woolen muffler", category: "Outfit" },
    { name: "Boots", category: "Accessories" },
    { name: "Octavia black", category: "Accessories" },
    { name: "Fancy earings", category: "Accessories" },
    { name: "Labyrinth boot", category: "Accessories" },

    // Add more items to your dataset
  ];

  const handleSearch = () => {
    const filteredResults = data.filter((item) =>
      item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(filteredResults);
  };

  return (
    <>
      <header className="header">
        <div className="container-fluid">
          <div className="row">
            <div className="col-xl-3 col-lg-2">
              <div className="header__logo">
                <font>eStore</font>
              </div>
            </div>
            <div className="col-xl-6 col-lg-7">
              <div className="input-group mt-3">
                <div className="input-group-prepend">
                  <select className="form-control btn btn-success dropdown-toggle">
                    <option>ALL</option>
                    <option>Outfit</option>
                    <option>Accessories</option>
                  </select>
                </div>

                <input
                  type="text"
                  className="form-control"
                  placeholder="Search..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />

                <div className="input-group-append">
                  <button className="btn btn-success" onClick={handleSearch}>
                    <i className="fa fa-search"></i>
                  </button>
                </div>
              </div>

              {searchResults.length > 0 && (
                <div className="mt-2">
                  <p>Search Results:</p>
                  <ul>
                    {searchResults.map((result, index) => (
                      <li key={index}>
                        {result.name} - {result.category}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>

            {/* ... (rest of your existing code) ... */}
          </div>
        </div>
      </header>
      <TopMenu />
    </>
  );
};

export default Header;
